package esample;

public class Op1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20, b=10, c=0;
		c =a + b;
		System.out.println(c);
		c =a-b;
		System.out.println(c);
		c =a*b;
		System.out.println(c);		
		c =a/b;
		System.out.println(c);
		c =a%b;
		System.out.println(c);
		c = 12%5;
		System.out.println(c);
		
		System.out.println("12의 일의 자리의 값은?");
		c= 12%10;
		System.out.println(c);
		
		System.out.println("23의 일의 자리의 값은?");
		c= 23%10;
		System.out.println(c);
		

	}

}
