package esample;

public class Op2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5, j=5;
		System.out.println(i++);
		System.out.println(i);
		System.out.println(i++);
		System.out.println(j--);
		System.out.println(j);
		System.out.println(j--);
		System.out.println("i = " + i +", j =" +j);
		
	
		
		
			
	}	
	
}
