package esample;

public class Op2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5, j;
		j=i++;
		System.out.println("j="+j);
		j=++i;
		System.out.println("j="+j);
		j=i--;
		System.out.println("j="+j);
		j=i--;
		System.out.println("j="+j);
		
		System.out.println("ÇöÀç i="+j);
		System.out.println("1++ : "+i++);
		System.out.println("++i : "+(++i));
		System.out.println("i-- : "+i--);
		System.out.println("--i : "+(--i));

	}

}
