package exsampie;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c1 ='a';
		//char c2 = c1+1;
		char c3 = (char)(c1+1);
		System.out.println(c3);
		char c4 =++c1;
		System.out.println(c4);
		int i = 'B'-'A';
		System.out.println(i);
		int j = '2'-'0';
		System.out.println(j);
		
		int share=10/8;
		int remain = 10%8;
		System.out.println(share);
		System.out.println(remain);

	}

}
