package exsampie;

public class Op3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =10;
		int y = 8;
		
		System.out.printf("%d울 %d로 나누면,\n, x",x,y);
		System.out.printf("몫은 %d이고, 나머지는 %d입니다.\n",x/y,x%y);
		
		
		int a= 10, b= 20;
		System.out.println(a==b);
		System.out.println(a!=b);
		System.out.println(a>b);
		System.out.println(a>=b);
		System.out.println(a<b);
		System.out.println(a<=b);
		
		

	}

}
