
public class switch01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		switch (a) {
		case 10:
			System.out.println("10이다");
			break;
		case 20:
			System.out.println("20이다");
			break;
		case 30:
			System.out.println("30이다");
			break;
		default:
			System.out.println("그 외의 갑");
			break;
		}

	}

}
